Style:
/* SET MARGIN AND PADDING TO 0 FOR ALL ELEMENTS */
* {
margin: 0; padding: 0;
}

/* SET BASE FONT ATTRUBUTES */
html, body {
font-family: Georgia, "Times New Roman", Times, serif;
font-size: 1.5em;
color: #000;
}

/* REMOVE PADDING AND MARGIN VALUES */
html, body, h1, h2, h3, h4, h5, h6, p, ul, ol, dl, li, dd, dt, img,
blockquote, q, table, thead, tbody, tfoot, caption, th, tr, td, a, form,
input, textarea, fieldset, pre
{margin: 0; padding: 0;}

/* SORT OUT header-FORMATTING AND SIZES */
h1, h2, h3, h4, h5 {font-weight: bold;}

h1 {font-size: 2em;}
h2 {font-size: 1.75em;}
h3 {font-size: 1.5em;}
h4 {font-size: 1.2em;}
h5 {font-size: 1.1em;}
h6 {font-size: 1em;}

/* HARMONIZE LINKS, KILL BORDER ON IMG LINKS */
a {text-decoration: underline;}
a:link, a:visited {color: #00f;}
a:hover {color: #33f;}
a:active {color: #fff;}
a img, :link img, :visited img {border: none}

/* REMOVE BROWSERS DEFAULT TABLE BORDERS */
table {border-collapse: collapse;}

/* REMOVE AUTOMATIC TOP/BOTTOM MARGINS ON NESTED LISTS */
ul ul, ul ol, ul dir, ul menu, ul dl,
ol ul, ol ol, ol dir, ol menu, ol dl,
dir ul, dir ol, dir dir, dir menu, dir dl,
menu ul, menu ol, menu dir, menu menu, menu dl,
dl ul, dl ol, dl dir, dl menu, dl dl
{margin-top: 0; margin-bottom: 0;}

/* HARMONISE LIST-BULLET TYPE */
ul, ol, dl {list-style-type: disc;}

body {
	background: #666;
}

body, input, textarea, select {
	font: normal small "Trebuchet MS", Arial, Helvetica, sans-serif;
	color: #333;
	margin: 5px auto;
}

h1, h2, h3 {
	margin-bottom: 1em;
	font-weight: normal;
	color: #579700;
}

h1 {
	font-size: 2.2em;
}

h2 {
	font-size: 1.7em;
}

h3 {
	font-size: 1em;
	font-weight: bold;
}

p, blockquote, ul, ol, form {
	margin-bottom: 1.5em;
}

blockquote, ul, ol {
	margin-left: 3em;
}

blockquote {
	margin-right: 3em;
	font-style: italic;
}

ul {
	list-style-type: square;
}

a {
	color: #579700;
}

a:hover {
	text-decoration: none;
}

a:link, a:visited {color: #579700;}


/* Wrapper */

#wrapper {
	width: 942px;
	margin: 0px auto;
	background-color: #FFFFFF;
}

/* header-*/

#header {
	height: 257px;
	width: 942px;
	position: relative;
	background: #CDE691 url(images/paddy.jpg) no-repeat;
	margin: 0px auto;
}

#header h3 {
	color: aliceblue;
}

#header h2 {
	position: absolute;
	top: 183px;
	left: 10px;  
	font-size: 3em;
	letter-spacing: -2px;
}

#header h3 {
	position: absolute;
	top: 225px;
	left: 20px;  
	font-size: 1em;
}

#header a {
	text-decoration: none;
	color: #FFF;
}

#header:after {
	content: ".";
	display: block;
	height: 0;
	clear: both;
	visibility: hidden;
}

/* Menu */

#menu
{
	width: 928px;
	position: absolute;
	margin: -250px 0;
}

#menu ul
{
	list-style: none;
	text-align: right;
	padding: 10px 0px 10px 0px;
	margin: 0;
}

#menu li
{
	padding: 0em 0.7em 0em 0.7em;
	display: inline;
}

#menu li a
{
	color: #FFFFFF;
	font-weight: bold;
	text-decoration: none;
	border-bottom: none;
	font-size: 1.2em;
}

#menu li a.active
{
	border-bottom: 4px solid #FFFFFF;
}

#menu li a:hover
{
	border-bottom: 4px solid #FFFFFF;
}

/* Content */

#content {
	background: #FFFFFF url(images/bg.gif) repeat-y;
	border-top: 5px solid #8CC405;
	border-bottom: 1px solid #8CC405;
	margin: 0px auto;
	width: 928px;
}

/* Blog */

#blog {
	float: right;
	width: 678px !important;
	width: 675px; /*IE6 3px problem */
	margin: 0px auto;
}

/* Post */

.post {
	padding-bottom: 20px;
}

.post .title {
	height: 50px;
	border-top: 1px solid #8CC405;
	text-transform: uppercase;
	font-size: small;
	font-weight: bold;
	padding: 5px;
	background: #FFF url(images/cbg.gif) repeat-x;
}

.post .title a {
	text-decoration: none;
}

.post .title a:hover {
	text-decoration: underline;
}

.post .date {
	margin-top: -73px;
	text-align: right;
	font-size: x-small;
	padding: 5px;
	color: #579700;
}

.post .entry {
	padding: 10px 15px 10px 15px;
	line-height: 1.8em;
}

.post p {
	text-align: justify;
}

.post .meta {
	padding: 0 15px;
	font-size: x-small;
	color: #333;
}

.entry h1, .entry h2, .entry h3 {
	color: #333;
}

/* Sidebar */

#sidebar {
	float: left;
	width: 250px;
	padding-top: 5px;
	margin: 0px auto;
	border-top: 1px solid #8CC405;
}

#sidebar ul {
	margin: 0;
	list-style: none;
	padding: 0 10px;
}

#sidebar li {
	margin-bottom: 30px;
}

#sidebar li ul {
	line-height: 1.5em;
	list-style-type: none;
	padding-left: 10px;
}

#sidebar li li {
	margin: 0;
	color: #579700;
}

#sidebar h2 {
	background: #CDE691 url(images/sbg.gif) repeat-x;
	border-bottom: 3px solid #8CC405;
	text-transform: uppercase;
	font-size: small;
	font-weight: bold;
}

#sidebar a {
	text-decoration: none;
	color: #579700;
	padding-left: 15px;
	background: transparent url(images/listoff.gif) left center no-repeat;
}

#sidebar a:hover {
	text-decoration: none;
	color: #005300;
	background: transparent url(images/liston.gif) left center no-repeat;
}

