body {
	background: #FF2D5A url('images/body.png') repeat-x scroll top left;
	margin:0;
	padding:0;
	font-family: Verdana, Geneva, sans-serif;
	font-size: 13px;
	color: #666;
}

* {
	margin:0;
	padding:0;
}

/** element defaults **/
table {
	width: 100%;
	text-align: left;
}

th, td {
	padding: 10px 10px;
}

th {
	color: #fff;
	background: #FF2D5A none repeat-x scroll left top;
}

td {
	border-bottom: 1px solid #ccc;
}

code, blockquote {
	/*display: block;
	border-left: 5px solid #222;
	padding: 10px;
	margin-bottom: 20px;*/
}
code {
	/*background-color: #222;*/
	color:#633;
	border: none;
}
blockquote {
	border-left: 5px solid #222;
}

blockquote p {
