#container {
	width: 960px;
	margin: 0 auto;
	padding: 20px 10px;
	background-color: #fff;
}

#header {
	padding: 0px 15px;
	background: #333;
	margin: 0 auto;
	height: 100px;
	border: 1px solid #444;
}

#header h1 {
	padding: 0; 
}
#header h1 a {
	color: #fff;
	padding-top: 35px;
	font-size: 40px;
	font-weight: normal;
	text-transform: uppercase;
	text-decoration: none;
	letter-spacing: -3px;
	float: left;
	text-shadow: 2px 2px #111;
}

#header h2 {
	color: #777777;
	float: left;
	font-family: Arial,Helvetica,sans-serif;
	font-size: 13px;
	font-weight: bold;
	letter-spacing: 0;
	margin-left: 5px;
	margin-top: 53px;
	padding-bottom: 0;
	padding-left: 4px;
}

#nav {
	height: 36px;
	width: 960px;
	background: #111 url('images/nav-bg.jpg') repeat-x scroll;
	position: relative;
	z-index: 999;
}

#nav ul {
	list-style: none;
	padding: 0;
	margin: 0;
	height: 36px;
}

#nav ul li {
	display: block;
	float: left;
}

#nav ul li a {
	display: block;
	color: #ddd;
	display: block;
	font-size: 11px;
	font-weight: bold;
	float: left;
	text-transform: uppercase;
	font-family: Lucida, "Lucida Sans", Geneva, Arial, sans-serif;
	padding: 13px 15px 12px;
	text-decoration: none;
}

#nav ul li a.has_submenu {
	background: transparent url('images/submenu-item.gif') no-repeat scroll right center;
	padding-right: 30px;
}

#nav ul li a.has_submenu:hover, #nav ul li.sfHover a.has_submenu {
	background-image: url('images/submenu-item-hover.gif');
}

#nav ul ul li a { 
	float: none; 
	border-top: 1px solid #333;
	border-bottom: 1px solid #333;
	width: 170px;
}

#nav ul li.selected li a { 
	text-decoration: none; 
	color: #ddd;
}

#nav ul li.selected a, #nav ul li.selected a:hover, #nav ul li.selected li.selected a  {
	text-decoration: underline;
	color: #fff;
}

#nav ul li a:hover, #nav ul li.sfHover a, #nav ul ul a {
	color: #fff;
	background-color: #333;
}

#nav ul ul li a:hover {
	background-color: #000;
	border-top: 1px solid #555;
	border-bottom: 1px solid #555;
}

/*** ESSENTIAL STYLES ***/
.sf-menu, .sf-menu * {
	margin:			0;
	padding:		0;
	list-style:		none;
}
.sf-menu {
	line-height:	1.0;
}
.sf-menu ul {
	position:		absolute;
	top:			-999em;
	width:			200px; /* left offset of submenus need to match (see below) */
	margin-top: 	10px;
}
.sf-menu ul li {
	width:			100%;
}
.sf-menu li:hover {
	visibility:		inherit; /* fixes IE7 'sticky bug' */
}
.sf-menu li {
	float:			left;
	position:		relative;
}
.sf-menu a {
	display:		block;
	position:		relative;
}
.sf-menu li:hover ul,
.sf-menu li.sfHover ul {
	left:			0;
	top:			2.5em; /* match top ul list item height */
	z-index:		99;
}
ul.sf-menu li:hover li ul,
ul.sf-menu li.sfHover li ul {
	top:			-999em;
}
ul.sf-menu li li:hover ul,
ul.sf-menu li li.sfHover ul {
	left:			200px; /* match ul width */
	top:			0;
}
ul.sf-menu li li:hover li ul,
ul.sf-menu li li.sfHover li ul {
	top:			-999em;
}
ul.sf-menu li li li:hover ul,
ul.sf-menu li li li.sfHover ul {
	left:			10em; /* match ul width */
	top:			0;
}


/* front page slider styles */
div#jFlowSlide {
	margin:0 auto;
}
div#slides-container {
	height: 245px;
	overflow: hidden; 
	width: 960px; 
	border-bottom: 1px solid #ddd;
}
div#slides-container div#jFlowSlide {
	height: 225px;
	padding-top: 15px;
}
div#slides-container div.jFlowSlideContainer img {
	margin:auto;
	border:4px solid #313D45;
}
div#slides-container div.jFlowSlideContainer div {
}
div#slides-container div.jFlowSlideContainer div.slide-image {
	float:left;
	width: 300px;
	padding-top:10px;
}
span.jFlowPrev, span.jFlowNext {
	background-image:url('images/slide-prev.gif');
	background-repeat:no-repeat;
	display:block;
	height:34px;
	width:21px;
	float:left;
	margin:0;
	cursor:pointer;
}
span.jFlowPrev span, span.jFlowNext span { display:none; }
span.jFlowNext {
	background-image:url('images/slide-next.gif');
	float:right;
}
div#slides-container div.controls {
	position:relative;
	top:-125px;
	width:960px;
	margin:0 auto;
}
div.slide-text {
	padding-top: 10px;
}
