Style:
/* RESETS & BASIC PAGE SETUP */
* { margin: 0; padding: 0; }

html, body, div, span, applet, object, iframe,
h1, h2, h3, h4, h5, h6, p, blockquote, pre,
a, abbr, acronym, address, big, cite, code,
del, dfn, em, font, img, ins, kbd, q, s, samp,
small, strike, strong, sub, sup, tt, var,
b, u, i, center,
dl, dt, dd, ol, ul, li,
fieldset, form, label, legend,
table, caption, tbody, tfoot, thead, tr, th, td {
	margin: 0;
	padding: 0;
	border: 0;
	outline: 0;
	font-size: 100%;
	vertical-align: baseline;
	background: transparent;
}
body {
	line-height: 1;
}
ol, ul {
	list-style: none;
}
blockquote, q {
	quotes: none;
}
blockquote:before, blockquote:after,
q:before, q:after {
	content: '';
}

/* remember to define focus styles! */
:focus {
	outline: 0;
}

/* tables still need 'cellspacing="0"' in the markup */
table {
	border-collapse: collapse;
	border-spacing: 0;
}

fieldset,img { 
	border:0;
}
address,caption,cite,code,dfn,em,strong,th,var {
	font-style:normal;
	font-weight:normal;
}
ol,ul {
	list-style:none;
}
caption,th {
	text-align:left;
}
h1,h2,h3,h4,h5,h6 {
	font-size:100%;
	font-weight:normal;
}

ul { list-style: none inside; }

a {
	outline: none;
	text-decoration: none;
}
a img { border: none; }

/* END RESET */
/*GENERAL*/
h1 {
	color: #f8f8f8;
	font-family: Garamond, Georgia, Times, Times New Roman, serif;
	font-size: 60px;
	font-weight: bold;
}
h1 a{
	color: #f8f8f8;
	text-decoration: none;
}

h2 {
	color: #f8f8f8;
	font-family: Garamond, Georgia, "Times New Roman", serif;
	font-size: 50px;
	font-weight: bold;
	line-height: 54px;
}
h2 a{
	color: #f8f8f8;
	text-decoration: none;
}
h2 a:hover{
	color: #736f5e;
}

h3 {
	color: #f8f8f8;
	font-family: Garamond, Georgia, "Times New Roman", serif;
	font-size: 38px;
	line-height: 41px;
	font-weight: bold;
}
h3 a{
	color: #f8f8f8;
	text-decoration: none;
}
h3 a:hover{
	color: #736f5e;
}

h4 {
	color: #f8f8f8;
	font-family: Garamond, Georgia, Times, "Times New Roman", serif;
	font-size: 23px;
}

p {
	font-family: Garamond, Georgia, Times, "Times New Roman", serif;
	font-size: 15px; 
	line-height: 24px;  
}
pre, code{
	line-height: 20px;
}


/*HEADER*/
div#logo p span.slogan{
	font-family: Georgia, Palatino, "Palatino Linotype", Times, "Times New Roman", serif;
	font-size: 12px;
	font-style: italic;
	color: #656565;
	letter-spacing: 1.0em;
}

div#nav-bar ul#nav1 li a{
	color: #f8f8f8;
	font-family: Garamond, Georgia, Times, "Times New Roman", serif;
	text-transform: uppercase;	
	font-size: 18px;
}
div#nav-bar ul#nav1 li a:hover{
	color: #fff;
text-shadow: 1px 1px 7px #333;
}

div#nav-bar div#nav2 p.rss a{
	color: #f8f8f8;	
	font-size: 16px;
	text-decoration: underline;
}
div#nav-bar div#nav2 p.rss a:hover{
	text-decoration: none;
	color: #666;	
}

form#search-form input.search-field, form#search-form input.search-button{
	font-family: Garamond, Georgia, Times, "Times New Roman", serif;
	font-size: 14px;
}

/*CONTENT*/

div.featured p{
	font-size: 16px; 
	line-height: 24px;  
}

.featured-meta,.post-meta {
	color: yellow;
}

span.comments a, span.more a {
	color: yellow;
	text-decoration:underline;
}
span.comments a:hover, span.more a:hover {
	color: #736F5E;
}

span.category {
	font-size: 15px;
	text-transform: uppercase;
}
span.category a{
	color: #666;
	text-decoration: underline;

}
span.category a:hover{
	text-decoration: none;
}

/*SIDEBAR*/
div#sidebar {
	font-family: Garamond, Georgia, Times, "Times New Roman", serif;
	font-size: 14px;
	line-height: 19px;
}
ul.category li a{
	line-height: 18px;
	text-transform: uppercase;
	text-decoration: underline;
	color: yellow;
}
ul.category li a:hover{
	text-decoration: none;
	color: darkorange;
}
ul.popular-posts li a{
	color: yellow;
}
ul.popular-posts li a:hover{
	text-decoration: underline;
	color: darkorange;
}
ul.social-links li a{
	font-style: italic;
	font-weight: bold;
	color: yellow;
}
ul.social-links li a:hover{
	text-decoration: underline;
	color: darkorange;
}
ul.twitter-updates li a{
	font-style: italic;	
	color: yellow;
}
ul.twitter-updates li{
	font-style: italic;	
	color: yellow;
}
ul.twitter-updates li span{
	font-style: normal;

}

/*FOOTER*/
p#footer-text {
	font-family: Georgia, Palatino, "Palatino Linotype", Times, "Times New Roman", serif;
	font-size: 11px;
	text-align: right;
}
p#footer-text a{
	text-decoration: underline;
	color: #000;
}
p#footer-text a:hover{
	color: darkorange;
}
#sidebar ul.twitter-updates li a {
	text-decoration: underline;
}
#sidebar ul.twitter-updates li a:hover {
	text-decoration: underline;
	color: darkorange;
}

body {
	background: #59e;
}
.clear {
	clear: both;
}

/*HEADER*/

div#logo {
	width: 960px;
	margin: 0 auto;
	padding: 30px 0 25px 0;
	text-align: center;	
}
div#logo p span.slogan{
	display: block;
	margin-top: 5px;
}
div#nav-bar {
	width: 960px;
	height: 50px;
	margin: 0 auto;
	border-top: 6px solid #000;
	border-bottom: 2px solid #000;
}
div#nav-bar ul#nav1{
	float: left;
}
div#nav-bar ul#nav1 li {
	float: left;
}
div#nav-bar ul#nav1 li a{
	display: block;
	margin: 15px 10px 0px 10px;
}
div#nav-bar div#nav2 {
	width: 335px;
	height: 50px;
	float: right;
}
div#nav-bar div#nav2 p.rss {
	display:block;
	height: 20px;
	width: 85px;
	margin-top: 12px;
	padding-top: 5px;
}
div#nav2 form#search-form { 
	float: right;
	margin-top: 12px;
	width: 230px;
}
div#nav2 form#search-form input.search-field{
	background-color:#f6f2e4;
	border:1px solid darkorange;
	padding: 3px;
	margin-right: 3px;
}
div#nav2 form#search-form input.search-button {
	border: none;
	padding-left: 20px;
	height: 21px;
	width: 45px;
}
div#nav2 form#search-form input.search-button:hover{
	border-bottom: 1px solid #000;
}

/*CONTENT*/
div#content {
	width: 100%;
}
div#main-content {
	width: 960px;
	margin: 25px auto 0;
	position: relative;
}
div#content-1 {
	width: 700px;
	float: left;
	padding: 0 10px;
}
div#content-1 div.featured {
	padding-bottom: 30px;
	border-bottom: solid 1px #403d32;
}
div#content-1 div.featured h2{
	display: block;
	margin-bottom: 25px;
}
div#content-1 div.featured img.featured-img{
	float: left;
	margin: 0 10px 0 0;
}
div#content-1 div.featured p{
	margin-top: 10px;
}
div#content-1 div.featured div.featured-meta{
	margin-top: 20px;
}
div#content-1 div.odd-post {
	width: 335px;
	float:left;
	margin-right: 30px;
	margin-bottom: 15px;
}
div#content-1 div.even-post {
	width: 335px;
	float: right;
	margin-bottom: 15px;
}
div#content-1 div.odd-post p,div#content-1 div.even-post p {
	margin-top: 10px;
}
div#content-1 div.post-header {
	margin: 15px 0;
}
div#content-1 div.post-header span.category {
	display: block;
	margin: 20px 0;
}
div#content-1 div.post-meta{
	margin-top: 15px;
}

/*SIDEBAR*/
div#sidebar {
	width: 200px;
	margin: 5px 10px 10px 0px;
	padding: 0 10px 20px;
	float: right;
	background: #369;
	display: inline;
}
div#sidebar h4 {
	display: block;
	margin: 25px 0 10px;
}
div#sidebar ul.popular-posts li{
	display: block;
	padding: 7px 0;
	border-bottom: 1px dotted #666666;
}
div#sidebar ul.twitter-updates li{
	display: block;
	padding: 7px 0;
}
/*FOOTER*/
div#footer {
	clear:both;
	width:100%;
	margin-top: 10px;
}
div#footer-content {
	width: 940px;
	margin: auto;
	border-top: 3px solid #000;
	padding: 20px 10px 35px;
}

a.paginatorcontrol {
background: #09F;
	border: 1px solid black;
	-moz-border-radius: 4px;
	-khtml-border-radius: 4px;
	-webkit-border-radius: 4px;
	border-radius: 4px;
	color: white;
	padding: 5px 9px;
	margin: 3px;
}
a.paginatorcontrol:hover{
	background:darkorange;
    	color:#fff;
}
